Below you can find the main code you can use to template. As I said a refactor is needed, but you can extract te core logic and functionalities of the interactive R Shiny framework:
library (shiny)library (ggplot2)library (tidyverse)source(&quot;helpers/ColourBorders.R&quot;)source(&quot;helpers/PlotMapGrid.R&quot;)ui &lt;-fluidPage(mainPanel(column(12,offset = 5, titlePanel(&quot;Pathfinding Algorithm Visualization using R!&quot;)),HTML(&quot;&amp;nbsp&quot;),column(12,offset = 5,HTML(&quot;&amp;nbsp&quot;),actionButton(&quot;go_search_actionButton&quot;, &quot;Go Search!&quot;),actionButton(&quot;clean_all_actionButton&quot;, &quot;Clean All&quot;)),HTML(&quot;&amp;nbsp&quot;),column(12,offset=5, plotOutput(&quot;map_grid_plotOutput&quot;,click=&quot;map_grid_plotOutput_click&quot;))))server &lt;-function(input, output){## Initial paramsmax_steps &lt;-50matrix_x_size &lt;-20matrix_y_size &lt;-20grid_map_reactive &lt;-matrix(ncol = matrix_x_size,nrow = matrix_y_size,data = 0) ## Colours Dict (in progress)# 1- Wall# 2- Init# 3- Obj# 4- Step done# 5- Goal achieved# Initialize objtsgrid_map_reactive[4,15] &lt;-3 # objgrid_map_reactive[17,3] &lt;-2 # initinitial_step &lt;-which(grid_map_reactive ==2,arr.ind = TRUE)grid_map_reactive &lt;-ColourBorders(grid_map_reactive, 1) # rounding wallsreact_df &lt;-reactiveValues(df = grid_map_reactive, # reactive initorig = grid_map_reactive,walls = grid_map_reactive)observe({if(!is.null(input$map_grid_plotOutput_click)){new_x_value &lt;-trunc(input$map_grid_plotOutput_click$x)new_y_value &lt;-trunc(input$map_grid_plotOutput_click$y)if(between(new_x_value,2,matrix_x_size-1) &amp;between(new_y_value,2,matrix_y_size-1)){isolate(react_df$df[new_y_value,new_x_value] &lt;-if_else(react_df$df[new_y_value,new_x_value]==0,1,0))isolate(react_df$df[4,15] &lt;-3)isolate(react_df$df[17,3] &lt;-2)isolate(react_df$df[17,3] &lt;-2)isolate(react_df$walls &lt;-react_df$df)output$map_grid_plotOutput &lt;-renderPlot({PlotMapGrid(react_df$df,matrix_x_size,matrix_y_size)}, width=600, height=600,position=&quot;center&quot;)}}}) # Go search! Pseudo-random pathfinding algortihmobserveEvent(input$go_search_actionButton,{if(nrow(which(react_df$df ==4, arr.ind = TRUE))&gt;=1) react_df$df &lt;-react_df$walls # click search without cleancurrent_step &lt;-initial_step obj &lt;-which(react_df$df ==3, arr.ind = TRUE)previous_steps_with_opt &lt;-current_stepfor(i in 1:max_steps){next_step_col &lt;-tribble(~row, ~col,current_step[1]+1,current_step[2]+0,current_step[1]+0,current_step[2]+1,current_step[1]-1,current_step[2]+0,current_step[1]+0,current_step[2]-1)next_values &lt;-NULLfor(r in 1:nrow(next_step_col)){next_values &lt;-c(next_values,react_df$df[next_step_col[[r,1]],next_step_col[[r,2]]])}if(3 %in%next_values){current_step &lt;-next_step_col[next_values==3,] %&gt;%as.matrix()react_df$df[current_step] &lt;-5break()} else if(0 %in%next_values){if(sum(next_values==0)&gt;1){previous_steps_with_opt &lt;-current_step}current_step &lt;-next_step_col[next_values==0,] %&gt;%sample_n(1) %&gt;%as.matrix()react_df$df[current_step] &lt;-4} else {current_step &lt;-previous_steps_with_opt}}})# Reset allobserveEvent(input$clean_all_actionButton,{react_df$df &lt;-react_df$origreact_df$walls &lt;-react_df$orig})# First paneloutput$map_grid_plotOutput &lt;-renderPlot({PlotMapGrid(react_df$df,matrix_x_size,matrix_y_size)}, width=550, height=600,position=&quot;center&quot;)}shinyApp(ui=ui, server = server)Here are the helpers to construct the grid:
ColourBorders &lt;-function(df, col_value){## Rounding walls # Params: df - Map grid# col_value - Colour to fill the rounding blocks# Return: df with the filled roundingsdf[1,] &lt;-col_valuedf[,1] &lt;-col_valuedf[nrow(df),] &lt;-col_valuedf[,ncol(df)] &lt;-col_valuereturn(df)}PlotMapGrid &lt;-function(df, matrix_x_size, matrix_y_size){## Plot the interactive grid # Params: df - Map grid# matrix_x_size - X_axis limit# matrix_y_size - Y_axis limit# Return: plot with the pathfindingplot &lt;-rbind(which(df==1, arr.ind = TRUE) %&gt;%cbind(fill_col=&quot;#623B17&quot;),which(df ==2, arr.ind = TRUE) %&gt;%cbind(fill_col=&quot;#13293D&quot;),which(df ==3, arr.ind = TRUE) %&gt;%cbind(fill_col=&quot;#ffff66&quot;),which(df ==4, arr.ind = TRUE) %&gt;%cbind(fill_col=&quot;#99ccff&quot;),which(df ==5, arr.ind = TRUE) %&gt;%cbind(fill_col=&quot;#1B998B&quot;)) %&gt;%data.frame(stringsAsFactors = F) %&gt;%transmute(y = as.numeric(row), x = as.numeric(col), fill_col=fill_col) %&gt;%ggplot(aes(x+0.5,y+0.5)) +geom_tile(width = 1, height = 1, fill = df$fill_col, col=&quot;black&quot;) +scale_y_reverse() +scale_x_continuous(breaks = seq(0, matrix_x_size, 1),limits = c(0+0.5, matrix_x_size+1.5), minor_breaks = NULL) +scale_y_continuous(breaks = seq(0, matrix_y_size, 1),limits = c(0+0.5, matrix_y_size+1.5),minor_breaks = NULL) +theme_linedraw()+theme(axis.title.x=element_blank(),axis.title.y=element_blank(),axis.text.x=element_blank(),axis.text.y=element_blank(),axis.ticks.x=element_blank(),axis.ticks.y=element_blank())return(plot)}
